//
export function js_isNumber( json: Object ): boolean {
    const type: string = typeof json;
    return /* equals */( <any>( ( o1: any, o2: any ) => {
        if ( o1 && o1.equals ) {
            return o1.equals( o2 );
        } else {
            return o1 === o2;
        }
    } )( type, 'number' ) );
}
